sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	// Checks to see if the date passed in is in the past
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	// Is date at after today
	isDateAfterToday: function(strDate) {
		// Create GlideDateTime object for dates
		var currdate = new GlideDateTime();
		var testdate1 = new GlideDateTime(strDate);

		var today = currdate.getLocalDate();
		var istoday = testdate1.getLocalDate();

		//check dates
		if (today.compareTo(istoday) == 0) {
			return true;
		}
		else {
			return false;
		}
	},



	type: 'NeedItUtils'
};
